mapper.select:
select o.id AS id, o.hotelId AS hotelId, o.hotelName AS hotelName, h.hotelLevel AS hotelLevel, h.address AS address, o.roomId AS roomId, r.roomTitle AS roomTitle, r.roomBedTypeId AS roomBedTypeId, o.count AS count, o.bookingDays AS bookingDays, o.checkInDate AS checkInDate, o.checkOutDate AS checkOutDate, o.specialRequirement AS specialRequirement, o.linkUserName AS linkUserName, o.payAmount AS payAmount, r.payType AS roomPayType, d.name as roomBedTypeName, r.isHavingBreakfast as isHavingBreakfast, dayofweek(o.checkInDate) as checkInWeek, dayofweek(o.checkOutDate) as checkOutWeek from itrip_hotel_order o,itrip_hotel h,itrip_hotel_room r,itrip_label_dic d
o.hotelId=h.id and o.roomId=r.id and d.id=r.roomBedTypeId
and o.id=#{id}

select
id as id,
orderType as orderType,
orderNo as orderNo,
hotelId as hotelId,
hotelName as hotelName,
orderStatus as orderStatus,
count as count,
checkInDate as checkInDate,
payAmount as payAmount,
payType as payType,
linkUserName as linkUserName,
creationDate as creationDate
from itrip_hotel_order


        and id=#{id}


        and userId=#{userId}


        and orderType=#{orderType}


        and orderNo like CONCAT ('%',#{orderNo},'%')


        and hotelId=#{hotelId}


        and hotelName=#{hotelName}


        and checkInDate=#{checkInDate}


        and orderStatus=#{orderStatus}


        and payAmount=#{payAmount}


        and linkUserName like CONCAT ('%',#{linkUserName},'%')


        and creationDate >= #{startDate}


        and creationDate <= #{endDate}


order by creationDate desc

    limit #{beginPos},#{pageSize}

select count(*) from itrip_hotel_order


        and id=#{id}


        and userId=#{userId}


        and orderType=#{orderType}


        and orderNo like CONCAT ('%',#{orderNo},'%')


        and hotelId=#{hotelId}


        and hotelName=#{hotelName}


        and checkInDate=#{checkInDate}


        and orderStatus=#{orderStatus}


        and payAmount=#{payAmount}


        and linkUserName like CONCAT ('%',#{linkUserName},'%')


        and creationDate >= #{startDate}


        and creationDate <= #{endDate}

select
        id as id,
        userId as userId,
        orderType as orderType,
        orderNo as orderNo,
        tradeNo as tradeNo,
        hotelId as hotelId,
        hotelName as hotelName,
        roomId as roomId,
        count as count,
        bookingDays as bookingDays,
        checkInDate as checkInDate,
        checkOutDate as checkOutDate,
        orderStatus as orderStatus,
        payAmount as payAmount,
        payType as payType,
        noticePhone as noticePhone,
        noticeEmail as noticeEmail,
        specialRequirement as specialRequirement,
        isNeedInvoice as isNeedInvoice,
        invoiceType as invoiceType,
        invoiceHead as invoiceHead,
        linkUserName as linkUserName,
        creationDate as creationDate,
        createdBy as createdBy,
        modifyDate as modifyDate,
        modifiedBy as modifiedBy,
        bookType as bookType
from itrip_hotel_order


                and id=#{id}


                and userId=#{userId}


                and orderType=#{orderType}


                and orderNo=#{orderNo}


                and tradeNo=#{tradeNo}


                and hotelId=#{hotelId}


                and hotelName=#{hotelName}


                and roomId=#{roomId}


                and count=#{count}


                and bookingDays=#{bookingDays}


                and checkInDate=#{checkInDate}


                and checkOutDate=#{checkOutDate}


                and orderStatus=#{orderStatus}


                and payAmount=#{payAmount}


                and payType=#{payType}


                and noticePhone=#{noticePhone}


                and noticeEmail=#{noticeEmail}


                and specialRequirement=#{specialRequirement}


                and isNeedInvoice=#{isNeedInvoice}


                and invoiceType=#{invoiceType}


                and invoiceHead=#{invoiceHead}


                and linkUserName like CONCAT ('%',#{linkUserName},'%')


                and creationDate=#{creationDate}


                and createdBy=#{createdBy}


                and modifyDate=#{modifyDate}


                and bookType=#{bookType}


 order by creationDate desc

    limit #{beginPos},#{pageSize}

